import './App.css';
import React from 'react';
import Gauntlet from './resources/gauntlet-empty.png';

function App() {
  const [time, setTime] = useState(Date.now());
function createCookie() {
    let date = new Date(Date.now())
    let cookie = new Date(date.toLocaleDateString())
    return cookie
}

function getElapsedTime(cookie) {
    let dateNow = new Date(Date.now())
    let dateNowReadable = new Date(dateNow.toLocaleDateString())
    let elapsedTime = new Date(cookie - dateNowReadable)
    console.log(elapsedTime)
    if (elapsedTime) {
    let readableTime = new Date(elapsedTime.toLocaleDateString())
    return readableTime
    }
    else return "null"
}

  React.useEffect(() => {
    // Runs after the first render() lifecycle
    createCookie();  
    const interval = setInterval(() => setTime(Date.now()), 1000);
    return () => {
      clearInterval(interval);
    };
  }, []);
  return (
    <div className="App">
      <h1 className="title">Collect The Infinity Stones</h1>
      <h2 className="desc">Gather stones based on how long its been since you last visited the website. <br/>To reset the timer press the 'reset' button.</h2>
      <h1 className="speaker">ðŸ”‡</h1>
      <h1>Elapsed Time: {getElapsedTime().toString()}</h1>
      <div className="gauntlet-flex">
      <img alt="the infinity gauntlet" src={Gauntlet} className="gauntlet"/>
      </div>
    </div>
  );
}

export default App;
