      <img alt="the infinity gauntlet" src={myText >= 86400 ? Gauntlet1Stone : Gauntlet || myText >= 259200 ? Gauntlet2Stone : Gauntlet1Stone && myText >= 604800 ? Gauntlet3Stone : Gauntlet2Stone} className="gauntlet"/>
      </div>
    </div>
  );
}

export default App;
